
import { Packet } from "../packet";
import * as assert from "assert";

class TestStore extends Packet {
}

// TODO: добавить decline
let vectors = [
    {
        name: "discover",
        hex: "",
        type: 1
    },
    {
        name: "offer",
        hex: "",
        type: 2
    },
    {
        name: "request",
        hex: "",
        type: 3
    },
    {
        name: "decline",
        hex: "",
        type: 4
    },
    {
        name: "ack",
        hex: "",
        type: 5
    },
    {
        name: "nak",
        hex: "",
        type: 6
    },
    {
        name: "release",
        hex: "",
        type: 7
    },
    {
        name: "inform",
        hex: "",
        type: 8
    }
];

describe("Packet", () => {

    vectors.forEach(vector => {
        it(vector.name, () => {
            let packet = Packet.fromBuffer(new Buffer(vector.hex, "hex"));
            assert.equal(packet.type, vector.type);
        });
        it(vector.name + " find", () => {
            let packet = Packet.fromBuffer(new Buffer(vector.hex, "hex"));
            let hostName = packet.find(12);
        });

        it(vector.name + " from buffer to string", () => {
            let packet = Packet.fromBuffer(new Buffer(vector.hex, "hex"));
            let pktString = packet.toString();
            let toBuffer = packet.toBuffer();
            assert.equal(toBuffer.toString("hex"), vector.hex);
        });
    });

    context("one time check up", () => {

        it("empty type", () => {
            let packet = new Packet();
            assert.equal(packet.type, 0);
        });

        it("toBuffer options without end", () => {
            let packet = Packet.fromBuffer(new Buffer(vectors[0].hex, "hex"));
            packet.options.pop();
            let toString = packet.toBuffer();
        });

        it("toString branch UNKNOWN", () => {
            let packet = Packet.fromBuffer(new Buffer(vectors[0].hex, "hex"));
            packet.options.shift();
            let toString = packet.toString();
            packet.toBuffer();
        });
    });

});
